import React, { Component } from 'react';
import Video from './components/Video'
import SideVideo from './components/SideVideo'
import VideoInfo from './components/VideoInfo'
import CommentSection from './components/CommentSection'
import axios from 'axios'
import './App.css'

//USED FOR VIDEO COMP.
const currentVideo = [
  "/Assets/Images/video-list-0.jpg"
]

//USED FOR VIDEOINFO COMP.
const mainVideo = {
  id: '#',
  title: 'BMX Rampage: 2018 Highlights',
  description: 'On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time -- eight years after his first Red Cow Rampage title',
  channel: ' Red Cow',
  views: '1,001,023',
  likes: '110,985',
  duration: '#',
  video: '#',
  timestamp: '12/18/2018',
};


//USED FOR COMMENTS INSIDE COMMENTSECTION COMP.
const comments = [
  {
    name: "Michael Jordan",
    timestamp: "02/23/2019",
    comment: "This is a comment",
    id: 123
  },
  {
    name: "Kobe Bryant",
    timestamp: "02/08/2019",
    comment: "This is another comment",
    id: 321
  },
  {
    name: "Vince Carter",
    timestamp: "01/15/2019",
    comment: "This is one last comment",
    id: 132
  }
]


//USED FOR SIDEVIDEO COMP.
const sideVideos = [
  {
  id: 'biking', 
  title: 'Become A Travel Pro In One Easy Lesson', 
  channel: 'Scott Cranmer', 
  image: '/Assets/Images/video-list-1.jpg' 
  },
  {
    id: 'travel', 
    title: 'Les Houches The Hidden Gem Of The Chamonix', 
    channel: 'Bernard Patrick', 
    image: '/Assets/Images/video-list-2.jpg' 
  },
  {
    id: 'medicine', 
    title: 'Travel Health Useful Medical Information For', 
    channel: 'Lizzie Burton', 
    image: '/Assets/Images/video-list-3.jpg' 
  },
  {
    id: 'travel', 
    title: 'Cheap Airline Tickets Great Ways To Save', 
    channel: 'Emily Harper', 
    image: '/Assets/Images/video-list-4.jpg' 
  },
  {
    id: 'travel', 
    title: 'Take A Romantic Break In A Boutique Hotel', 
    channel: 'Ethan Owen', 
    image: '/Assets/Images/video-list-5.jpg' 
  },
  {
      id: 'travel', 
      title: 'Choose The Perfect Accommodations', 
      channel: 'Lydia Perez', 
      image: '/Assets/Images/video-list-6.jpg' 
  },
  {
      id: 'cruises', 
      title: 'Cruising Destination Ideas', 
      channel: 'Scott Cranmer', 
      image: '/Assets/Images/video-list-7.jpg' 
  },
  {
      id: 'train', 
      title: 'Train Travel On Track For Safety', 
      channel: 'Scotty Cranmer', 
      image: '/Assets/Images/video-list-8.jpg' 
   },          
]


const api_key = "4abc64a1-403f-4d37-8ad8-5c49d60737b0"
const url = `https://project-2-api.herokuapp.com/videos?api_key=${api_key}`


class Home extends Component {
  state = {
    videos: []
  }

  componentDidMount() {
    axios
      .get(url)
      .then(response => {
        this.setState({
          videos: response.data
        })
      })
  }

  render() {
    return (
      <div>
        <Video currentVideo={currentVideo}/>
        <div className="videoArea">
          <VideoInfo mainVideo={mainVideo}/>
          <CommentSection comments={comments}/>
          <div className="sideVideo__desktop">
            <SideVideo sideVideos={this.sideVideos}/>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
