import React from 'react'
import '../global_css/comment_css/styles.css'

const comments = [
  {
    name: "Michael Jordan",
    timestamp: "02/23/2019",
    comment: "This is a comment"
  },
  {
    name: "Kobe Bryant",
    timestamp: "02/08/2019",
    comment: "This is another comment"
  },
  {
    name: "Vince Carter",
    timestamp: "01/15/2019",
    comment: "This is one last comment"
  }
]


class Comments extends React.Component {
  render() {
    const commentList = comments.map(comment => {
      return (
        <div className="profile">
        <p className="name">{comment.name}</p>
        <p className="date">{comment.timestamp}</p>
        <p className="comment">{comment.comment}</p>  
        </div> 
      )
    })
    return (
      <div className="posted__section">
        <div className="posted__comments">
          <div className="name__container">
            <div className="profile"></div>
            <div className="user__info">
              <p className="name">{comments[0].name}</p>
              <p className="date">{comments[0].timestamp}</p>
              <p className="comment">{comments[0].comment}</p> 
            </div>
          </div>  
        </div>
      </div>
    )
  }
}

export default Comments
